    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}
{% block stylesheets %}
    <link rel=\"stylesheet\" href=\"/article/css/articles.css\">
{% endblock %}

{% block body %}
<div id=\"create_article\">
    {% if editMode %}
    <h1>Modification de l'article</h1>
    {% else %}
    <h1>Création d'un article</h1>
    {% endif %}
    {{ form_start(formArticle) }}

    <div class=\"form-group\">
        {% if editMode %}
            {{ article.user.username}}
        {% else %}
        {{ user.username}}
        {% endif %}
        <label for=\"\">Titre</label><br>
        {{ form_errors(formArticle) }}
        {{ form_widget(formArticle.name, {'attr': {'placeholder': \"Titre de l'article\"}}) }}
    </div>

    <div class=\"form-group\">
        <label for=\"\">Contenu</label><br>
        {{ form_errors(formArticle.content) }}
        {{ form_widget(formArticle.content, {'attr': {'placeholder': \"Contenu de l'article\"}}) }}
    </div>

    <button type=\"submit\">
        {% if editMode %}
        Enregistrer
        {% else %}
        Ajouter un article
        {% endif %}
    </button>

    {{ form_end(formArticle) }}
</div>
{% endblock %}", "article/create.html.twig", "C:\\Users\\utilisateur\\Desktop\\ForumDemo\\ForumDemo\\templates\\article\\create.html.twig");
